import React, { useState } from "react";
import "../src/Dropdown.css";
// import Netforce from "./Netforce";
//import Speeddiff from "./Speeddiff";
// import Distancetrvl from "./Distance_trvl";
const DropdownBox=()=>{
    const [phyFomula, setFuncName]=useState("")
    const phyFunc =(event)=>{
    setFuncName(event.target.value);
    }
    return(
        <>
        <div className="box-body">
            <label>Select your Formula</label>
            <select name="formula" id="formula" onChange={phyFunc}>
                <option value="speed_difference" >Speed difference</option>
                <option value="distance_travelled">Distance travelled</option>
                <option value="net_force">Net Force</option>
            </select>
            <h1>{phyFomula}</h1>
        </div>
        </>
    )
}

export default DropdownBox;
