import React, { useState } from "react";
import "../src/Netforce.css";
function Netforce(){
    const [forceComponents, setForceComponents] = useState({
      mass:"",
      acceleration:"",
    })
    const inputEvent = (e)=>{
      console.log(e.target.value);
      setForceComponents(e.target.value);
    }

  return (
    <div className="inputBox-body">
      <h1>NET FORCE</h1>
    <form className="inputBox-form">
      <label>Enter Mass:
        <input
          type="text" 
          name="mass"
          onChange={inputEvent}
          value={forceComponents.mass}
          // onChange={(e) => setMass(e.target.value)}
        />
      </label>
      <br/>
      <label> Enter acceleration: 
          <input 
          type="text" 
          name="acceleration"
          onChange={inputEvent}
          value={forceComponents.acceleration}/>
          {/* // onChange={(e)=> setAcceleration(e.target.value)}/> */}
      </label> <br/>
      <div className="output">
        <p> mass = {forceComponents.mass}</p>
        <h5> acceleration = {forceComponents.acceleration}</h5>
      </div>
      <br/>
    </form>
    </div>
  )
}
export default Netforce;
